let base_url = "http://app.wenku8.com/android.php";

function Update() {
    try {
        let burl = "https://wenku8-relay.mewx.org/,";
        let by = "action=novellist&sort=lastupdate&page={{page}}&t=0";
        const timetoken = Packages.java.lang.System.currentTimeMillis();
        const base64Encode = (str) => {
            const bytes = new java.lang.String(str).getBytes("UTF-8");
            return Packages.android.util.Base64.encodeToString(bytes, Packages.android.util.Base64.NO_WRAP);
        };

        let body = {
            "body": `&appver=1.22&timetoken=${timetoken}&request=${base64Encode(by)}`,
            "charset": "UTF-8",
            "method": "POST"
        };

        return burl + JSON.stringify(body);

    } catch (e) {
        // é”™è¯¯å¤„ç†
